package CoreJavaConcepts;

public class IFElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age = 16;
		if(age>= 18) {
			System.out.println("The person is eligible for voting");
		}
		else {
			System.out.println("The person is not eligible for voting");
		}
	}

}
